    if ((ref->id == hash) && (ref->label->size == name_size))
    {
      if (strncasecmp((char *) ref->label->data, (char *) name, name_size) == 0)
      {
        bufrelease(ref->label);
        bufrelease(ref->link);
        bufrelease(ref->title);
        return ref;
