    ref_idx = ref_idx->next;
  }

  ref_idx = calloc(1, sizeof(struct link_ref));
  if (!(&ref[ref_idx]))
  {
    return 0;
  }
  ref->id = hash;
  ref->next = references[ref->id % 8];
  references[ref->id % 8] = &ref[ref_idx];
  return ref;
}

